mapper.select:
select userid, passwd, username, nickname, address, addressdetail, interdongcode, email, phone
from member
where userid = #{userId}

mapper.update:
update member
set passwd = #{passwd}, userName = #{username}, nickName = #{nickname}, address = #{address}, addressDetail = #{addressdetail}, interdongcode = #{interdongcode}, email = #{email}, phone = #{phone}
where userid = #{userid}

mapper.select:
select userid, passwd, username, nickname, address, addressdetail, interdongcode, email, phone
from member
where userid = #{userid} and passwd = #{passwd}
